import React, { Component } from 'react';
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';
import { Link, NavLink } from 'react-router-dom';
import './Nav2.css';



function getUserDataFromSession() {
    return JSON.parse(sessionStorage.getItem('teacher')) || '';
}
class Nav2 extends Component {
    constructor() {
        super();
        this.state = {
            teacherName: '',
            login: false,
        }
    }
    componentDidMount() {
        const hamburger_menu = document.querySelector(".hamburger-menu");

        const container = document.querySelector(".nav-container");

        hamburger_menu.addEventListener("click", () => {
            container.classList.toggle("active");
        })

        const userData = getUserDataFromSession();
        if (userData) {

            const name = `${userData.firstName} ${userData.lastName}`;
            this.setState({ teacherName: name, login: true });

        }
        else {
            this.setState({ teacherName: '', login: false });
        }
    }

    logOut = () => {
        sessionStorage.clear();
        window.location.reload();
    }


    render() {

        return (
            <>
                <div class="nav-container">
                    <div class="navbar">
                        <div class="menu">
                            <h3 class="logo">Class<span>Image</span></h3>
                            <div class="hamburger-menu">
                                <div class="bar"></div>
                            </div>
                        </div>
                    </div>
                    <div class="main-container">
                        <div class="main">
                            <header>
                                <div class="overlay">

                                <Container>
            <Switch>
              <Route path="/" exact component={Home}/>
              <Route path="/register" component={Register} />
              <Route path="/login" component={Login} />
              <Route path="/about" component={About}/>
              <Route path="/create" component={()=><CreateClass/>} />
              <Route path="/show" component={MyClasses} />
            </Switch>
          </Container>

                                </div>
                            </header>
                        </div>
                        <div class="shadow one"></div>
                        <div class="shadow two"></div>
                        {/* <div class="shadow three"></div> */}
                        <div class="shadow four"></div>
                    </div>
                    <div class="links">
                        <ul>
                            <li class="active">
                                <NavLink style={{ "--i": "0.05s" }} exact to="/">
                                    Home
                                </NavLink>
                            </li>
                            <li>
                                <NavLink style={{ "--i": "0.1s" }}  to="/show">
                                    My Classes
                                </NavLink>
                            </li>
                            <li>
                                <NavLink style={{ "--i": "0.15s" }}  
                                to="/create">
                                    Add Class
                                </NavLink>
                            </li>
                            <li>
                                <NavLink style= {{ "--i": "0.2s" }} to="/login" hidden={this.state.login}>
                                    Login
                                </NavLink>
                            </li>
                            <li>
                                <NavLink style= {{ "--i": "0.25s" }} to="/register" hidden={this.state.login}>
                                Register
                                </NavLink>
                            </li>
                        </ul>
                    </div>
                </div>

            </>
        )
    }
}

export default Nav2;
