import React,{Component} from 'react' ;


class History extends Component {

    constructor(){
        super();
        this.state={
            history:[]
        }
    }
    componentDidMount(){
        let url=`http://localhost:3000/classHistory/${this.props.selected_class}`;
        fetch(url)
        .then(response=>response.json())
        .then(historyData =>{
            this.setState({history:historyData});
        })
        .catch(e=>{console.log("error while getting history data \n",e)});
    }
    renderTable=()=>{
        let historyData=this.state.history;
        let rows =historyData.map( (history,ind) =>{
            return( 
            <tr  key={ind}>
                 <td>{history.date}</td>
                 <td><button onClick={()=>{this.showDitails()}}>showDetails</button></td>
            </tr>
             )
         });
    }
    render(){
        this.renderTable();

        return (
            <>
            <div className="table-responsive mt-3">
                <table className="table table-bordered" style={{ width : '100%' }}>
                    <thead className="text-dark" style={{ fontFamily : 'Lobster', letterSpacing : '2px' }}>
                        <tr>
                            <th>Student ID</th>
                            <th>Student Name</th>
                        </tr>
                    </thead>
                    <tbody id='body' style={{ fontFamily : 'Acme' , color : 'gray' }}>
                        {rows}
                    </tbody>
                </table>
            </div>
        </>
        )
    }
}
export default History ;
