import React, { Component } from 'react';
import './home.css';
import Aos from 'aos';
import "aos/dist/aos.css";
const timeImg = require( '../img/save-time.svg');

class Home extends Component {

    componentDidMount() {


            Aos.init({ duration: 2000 })

    }
    render() {
        return (
            <>
                <div className="main-div">

                    <div data-aos="fade-right" className="abstract">
                        <h1>Who we are? </h1>
                        <p>
                        <span>Faceify</span> may provide some interesting services which are related to the <span>face recognition</span> mechanism, like(take attendance of students, detect the strangers in a company, counting the presence people, ...etc.)
                        </p>
                        <img className="home-imgs" src={require('../img/about-us.png')} alt="Abstract" />
                        <div className="my-line"></div>
                    </div>

                    <div id="trigger1" data-aos="fade-left" className="abstract">
                        <h1>Why us?</h1>
                        <p>
                          <img src={timeImg}/>

                        </p>
                        {/* <img className="home-imgs" src={require('../img/background.png')} alt="Background" /> */}
                         <div className="my-line"></div>

                    </div>

                    <div data-aos="fade-down"   
                        data-aos-anchor="#trigger-left"
                        data-aos-anchor-placement="top-top"
                    >
                        <h1>OUR OBJECTIVES</h1>
                        <ol>
                            <li>Facilitate the process of taking attendance from all web site.</li>
                            <li>Make the taking attendance process reliable.</li>
                            <li>Reduce the time needed to take attendance.</li>
                            <li>Make it easy for teacher to review the history of class.</li>
                        </ol>
                        <img className="home-imgs" src={require('../img/goal.png')} alt="Goals" />
                        <div className="my-line"></div>
                    </div>

                </div>
            </>
        )

    }
}
export default Home;
