import React from 'react';
import { Component } from 'react';
import { Col, Row } from 'react-bootstrap';
import AddStudents from './AddStudents';

class EditClass extends Component {
    constructor(){
        super();
        this.state={
            rows:[],
            clickedAdd:false,
            isThereChanges:false,
            students:[]
        }
    }

    componentDidMount(){
        let {students} = this.props.classInfo;
        this.renderTable(students);
        this.setState({students});
    }

    renderTable=(students)=>{
        let rows = students.map((std) => {
            return (
                <tr id={`r${std.id}`} key={std.id}>
                    <td>{std.id}</td>
                    <td>{std.name}</td>
                    <td><button onClick={() => this.deleteStd(std.id)}>X</button></td>
                </tr>
            )
        });
        this.setState({rows});
    }

    deleteStd=(id)=> {
        let conf = window.confirm("Are you sure you want to delete ..");
        if (conf) {
            this.props.classInfo.students = this.props.classInfo.students.filter(std => {
                return std.id !== id;
            })
            let rows = this.state.rows;
            rows = rows.filter(std => std.key !== id);
            this.setState({
                rows,
                isThereChanges:true
            });
        }
    }

    addStd=(arr)=>{
        console.log("students array: ", this.props.classInfo.students);
        console.log("added array :",arr)
        let students = this.props.classInfo.students;
        let idsOfAllStudents = students.map(obj=>obj.id).join(' ');
        let isThereChanges = this.state.isThereChanges;
        arr.forEach(std => {
            if( ! idsOfAllStudents.includes(std.id)){
                this.props.classInfo.students.push(std);
                this.renderTable(students);
                isThereChanges=true;
            }
        });
        this.setState({
            clickedAdd:false,
            isThereChanges
        });

    }

    onAddStd=()=>{
        this.setState({clickedAdd:true});
    }

    onDeleteClass=()=>{

    }

    onConfirm=()=>{

    }

    onCancel=()=>{
        this.renderTable(this.state.students);
    }

    render(){
        let {rows} = this.state;
        return (
            <>
                <h2 className="f2 mt4 ">{this.props.classInfo.className}</h2>
                <>
                    <table className="table table-bordered mt5" style={{ width: '100%' }}>
                        <thead className="text-dark" style={{ fontFamily: 'Lobster', letterSpacing: '2px' }}>
                            <tr>
                                <th>Student ID</th>
                                <th>Student Name</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody id='body' style={{ fontFamily: 'Acme', color: 'gray' }}>
                            {rows}
                        </tbody>
                    </table>
                </>

                { ! this.state.clickedAdd && (<button className="btn f3 grow btn-warning br4 shadow" onClick={this.onAddStd}>Add Students</button>)}
                {   this.state.clickedAdd && (<AddStudents createNew={true} onAddStd={this.addStd} />)}

                { this.state.isThereChanges && (
                    <Row className="mv5 justify-content-center"  xs={1} md={2}>
                        <Col xs={12} mdOffset={3} md={3}><button className="ma2 btn f3 grow btn-success br4 shadow" onClick={this.onConfirm}>Confirm Changes</button></Col>
                        <Col xs={12} mdOffset={3}md={3}><button className="ma2 btn f3 grow btn-danger  br4 shadow " onClick={this.onCancel}>Cancel </button></Col>
                    </Row>
                )}
                <Row className="ma3 justify-content-start"  >
                    <Col className="pa0 justify-start flex" xs={12} md={12}><button onClick={this.onDeleteClass} className="ma2 btn f5 grow btn-danger  br4 shadow">Delete Class !</button></Col>
                </Row>
            </>
        )
    }

}
export default EditClass;
