import React from 'react';
import { Component } from 'react';
import Swal from 'sweetalert2';
import '../styles/registration.css';
import $ from 'jquery';
import ReactCardFlip from 'react-card-flip';
import TeacherRegister from './TeacherRegister';

class Registration extends Component {
    constructor() {
        super();
        this.state = {
            isForgotClicked: false,
            // showSentToken: false,
            email: '',
            newPass: '',
            hashedToken: '',
            token: '',
            route: null,
            singnIn: true,
            isFlipped: false
        }
    }

    componentDidMount() {


    }


    checkId = (event) => {

        event.preventDefault();
        const form = document.getElementById('signInForm');
        let user = { id: form.teacherId.value, password: form.teacherPassword.value };

        if (user.id && user.password) {
            checkTheUserData(user)
                .then(userData => {

                    if (isUserLoggedIn(userData)) {
                        storedUserInSession(userData);
                        goToHomePage();
                    }
                    else {
                        Swal.fire({
                            icon: 'error',
                            title: 'Oops ...',
                            html: '<h5 class="text-danger font-lobster mt-2">Email OR Password Is Wrong<br>Try again.</h5>',
                        })
                    }

                })
                .catch(e => { console.log("error during send user data to backend within the sign in page.") });
        }
        else {
            Swal.fire({
                icon: 'warning',
                title: 'Hmmm ..',
                html: '<h5 class="text-warning font-lobster mt-2">All fields are required</h5>',
            })
        }

    }

    resetPassHandler = (e) => {
        let token = document.getElementById('resetToken').value;
        let { email, newPass, hashedToken } = this.state;
        let bodyData = { email, newPass, token, hashedToken };
        let baseUrl = document.getElementById('baseUrl').defaultValue;
        let url = `${baseUrl}/resetPass`;
        let requestOptions =
        {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(bodyData),
        };
        fetch(url, requestOptions)
            .then(res => res.json())
            .then(response => {
                if (response === 'done') {
                    Swal.fire({
                        icon: 'success',
                        title: 'Success',
                        html: '<h5 class="text-success font-lobster mt-2">Password Is Changed Successfully</h5>',
                    })
                    this.setState({ isForgotClicked: false });
                    this.clearForms();
                }
                else if (response === 'Token is wrong.') {
                    Swal.fire({
                        icon: 'error',
                        title: 'Oops ...',
                        html: '<h5 class="text-danger font-lobster mt-2">Token is wrong! <br> Try again..</h5>',
                    })
                }

                console.log(response);
            })

    }

    showResetForm = () => {
        this.setState({ isForgotClicked: true });
    }

    getAccessToken = (e) => {
        e.preventDefault();
        let resetForm = document.getElementById('resetPassForm');
        let email = resetForm.email.value;
        let newPass = resetForm.newPass.value;
        if (email && newPass) {
            this.setState({ email, newPass });
            let baseUrl = document.getElementById('baseUrl').defaultValue;
            let url = `${baseUrl}/sendToken`;
            let requestOptions =
            {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ email }),
            };
            fetch(url, requestOptions)
                .then(result => result.json())
                .then(response => {
                    if (response === 'This email is not registered yet.') {
                        Swal.fire({
                            icon: 'error',
                            title: 'Oops ...',
                            html: `<h5 class="text-danger font-lobster mt-2">${response}</h5>`,
                        })
                    }
                    else if (response === 'error within checking the teacher existance') {
                        Swal.fire({
                            icon: 'error',
                            title: 'Oops ...',
                            html: `<h5 class="text-danger font-lobster mt-2">Something wrong, try again later, please.</h5>`,
                        })
                    }
                    else {
                        this.setState({ hashedToken: response, showSentToken: true });
                        Swal.fire({
                            title: '<h3 class="font-acme">Enter the verification code sent to your email :</3>',
                            icon: 'question',
                            iconHtml: '?',
                            html: '<input type="text" id="resetToken" class="form-control text-center" placeholder="Verification Code" name="token"/>',
                        }).then(() => {
                            this.resetPassHandler();
                        });

                    }
                })
                .catch(e => { console.log("error when getting the hashed token..") });
        }
        else {
            Swal.fire({
                icon: 'warning',
                title: 'Hmmm ..',
                html: '<h5 class="text-warning font-lobster mt-2">All fields are required</h5>',
            })
        }

    }

    clearForms = () => {
        let form = document.getElementById('signInForm');
        let form2 = document.getElementById('resetPassForm');
        form.teacherId.value = '';
        form.teacherPassword.value = '';
        form2.email.value = '';
        form2.newPass.value = '';

    }
    changeForm = (flag) => {

       this.setState({ isFlipped: flag });
        if(flag){
            $('p.regBtn').removeClass('btnFormActive');
            $('p.signBtn').addClass('btnFormActive');
        }
        else{
            $('p.signBtn').removeClass('btnFormActive');
            $('p.regBtn').addClass('btnFormActive');
        }
    }

    render() {
        return (
            <>
                <div className="reg-body ">


                    {/* <h1>Login & registration Form</h1>
                    <div className="reg-info"><a href="https://www.grandvincent-marion.fr" target="_blank"><p> Made with <i className="fa fa-heart"></i> by Marion Grandvincent </p></a>
                    </div> */}

                    <div className="reg-content">
                        <div className="reg-container">
                            {/* <img className="bg-img" src="https://mariongrandvincent.github.io/HTML-Personal-website/img-codePen/bg.jpg" alt="" /> */}
                            <div className="reg-menu">
                                <p onClick={()=>{this.changeForm(false)}} className="  regBtn pointer flipperBtn btnFormActive ">SIGN IN</p>
                                <p onClick={()=>{this.changeForm(true)}} className=" signBtn pointer  flipperBtn ">SIGN UP</p>
                            </div>
                            <div className="my-line"></div>

                            <ReactCardFlip className="flipCard" isFlipped={this.state.isFlipped} flipDirection="horizontal">


                                <div className="connexion">


                                    <label>USERNAME</label>
                                    <input placeholder="" type="text" />

                                    <label>PASSWORD</label>
                                    <input placeholder="" type="text" />
                                    <button className="mt4 my-button ">Sign In</button>


                                    <p className="mt4 pointer grow " target="_blank"><h4>Forgot password?</h4></p>
                                </div>

                                <div className="enregistrer active-section">
                                    <div className="contact-form">
                                        <div className="reg-check reg-as">
                                            <label className="reg-as">
                                                <input id="check" type="checkbox" className="checkbox" />
                                                <svg xmlns="http://www.w3.org/2000/svg" width="26px" height="23px">
                                                    <path className="path-back" d="M1.5,6.021V2.451C1.5,2.009,1.646,1.5,2.3,1.5h18.4c0.442,0,0.8,0.358,0.8,0.801v18.398c0,0.442-0.357,0.801-0.8,0.801H2.3c-0.442,0-0.8-0.358-0.8-0.801V6" />
                                                    <path className="path-moving" d="M24.192,3.813L11.818,16.188L1.5,6.021V2.451C1.5,2.009,1.646,1.5,2.3,1.5h18.4c0.442,0,0.8,0.358,0.8,0.801v18.398c0,0.442-0.357,0.801-0.8,0.801H2.3c-0.442,0-0.8-0.358-0.8-0.801V6" />
                                                </svg>
                                            </label>
                                            <h3>Register As Student</h3>
                                        </div>
                                            <div className="my-line"></div>

                                            <TeacherRegister/>


                                    </div>
                                </div>
                            </ReactCardFlip>

                        </div>

                    </div>
                </div>

            </>
        );
    }
}
export default Registration;



//helper functions:


function isUserLoggedIn(userData) {
    if (userData.length === 1) {
        return true;
    }
    return false;
}

function storedUserInSession(userData) {
    const { firstName, lastName, id_number } = userData[0];
    const storedData = { firstName, lastName, id_number };
    sessionStorage.setItem('teacher', JSON.stringify(storedData));
}

function goToHomePage() {
    window.location.replace("http://localhost:3000");
}

function checkTheUserData(user) {
    let baseUrl = document.getElementById('baseUrl').defaultValue;
    return fetch(`${baseUrl}/loginTeacher`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(user),
    })
        .then(res => res.json())

}
