import React, { Component } from 'react';
import { Redirect, Route, useRouteMatch } from 'react-router-dom';
import Details from './Details';


class History extends Component {

    constructor() {
        super();
        this.state = {
            history: [],
            students: [],
            allStudents: [],
            imgs: [],
            date: '',
            showDitails: false,
            route: null
        }
    }
    componentDidMount() {
        console.log(this.props.selected_class, "aaaaa");
        let url = `http://localhost:3000/classHistory/${this.props.selected_class}`;
        fetch(url)
            .then(response => response.json())
            .then(classData => {
                console.log("calss Data:", classData)
                let allStudents = classData.students.map(std => {
                    return {
                        id: std.id_number,
                        name: std.firstName + ' ' + std.lastName
                    }
                })
                this.setState({
                    history: classData.history,
                    allStudents: allStudents
                });
            })
            .catch(e => { console.log("error while getting history data \n", e) });
    }
    renderTable = () => {
        let historyData = this.state.history;
        let rows = historyData.map((history, ind) => {
            return (
                <tr key={ind}>
                    <td className="f3 ">{history.date}</td>
                    <td><button className="btn f4 btn-dark shadow btn-submit grow " onClick={() => { this.showDitails(history._id) }}>showDetails</button></td>
                </tr>
            )
        });
        return rows;
    }
    showDitails = (classID) => {
        let history = this.state.history;
        history.forEach(hist => {
            if (classID === hist._id) {
                this.setState({
                    students: hist.students,
                    imgs: hist.images,
                    date: hist.date,
                    showDitails: true,
                    route: '/show/classHistory/details'
                })
            }
        })

    }
    render() {
        let rows = this.renderTable();
        let { students, allStudents, imgs, date } = this.state;
        let className = this.props.selected_class;
        let historyData = { students, allStudents, imgs, date, className };
        console.log("hist data inside render: \n",historyData)
        return (
            <>
                {this.state.route? <Redirect to={this.state.route}/> : null}
                <Route path={`/show/classHistory/details`} exact component={() =>
                { 
                    console.log("history data inside details: \n",historyData);
                return  <Details historyData={historyData} />
                } } />

                <Route path={`/show/classHistory`} exact>
                    <>
                        <div className="table-responsive mt-3">
                            <table className="table table-bordered" style={{ width: '100%' }}>
                                <thead className="text-dark f2" style={{ fontFamily: 'Lobster', letterSpacing: '2px' }}>
                                    <tr>
                                        <th>Date</th>
                                        <th>Details</th>
                                    </tr>
                                </thead>
                                <tbody id='body' style={{ fontFamily: 'Acme', color: 'gray' }}>
                                    {rows}
                                </tbody>
                            </table>
                        </div>
                    </>
                </Route>
            </>
        )
    }
}
export default History;
