import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import './CreateClass.css';
import { Redirect } from "react-router-dom";
import AddStudents from './AddStudents';
import Swal from 'sweetalert2';
import './table.css';

class CreateClass extends Component {
    constructor() {
        super();
        this.state = {
            isClickedNext: false,
            image: null,
            url: '',
            search: '',
            courseName: '',
            show: false,
            title: '',
            body: '',
            redirect: false,
            route: null
        }
    }

    //To Check if LoggedIn .
    checkLoggedIn = () => {
        const data = sessionStorage.getItem('teacher');
        if (!data) {
            this.setState({ route: '/login' });
            return false;
        }
        else {
            return true;
        }
    }

    opensweetalertdanger = () => {
        Swal.fire({
            icon: 'error',
            title: 'Oops ..! ',
            html: '<h5 class="text-danger">Class Name is already Exist !</h5>',
        })
    }

    next = (e) => {
        let baseUrl = document.getElementById('baseUrl').defaultValue;
        fetch(`${baseUrl}/checkClass/${this.state.courseName}`)
            .then(res => res.json())
            .then(data => {
                console.log(data);
                if (data) {
                    this.opensweetalertdanger();
                }
                else {
                    this.setState({ isClickedNext: true });
                }
            })
    }

    componentDidMount() {
        if (this.checkLoggedIn()) {
            let baseUrl = document.getElementById('baseUrl').defaultValue;
            fetch(`${baseUrl}/students`)
                .then(res => res.json()).then(data => {
                    this.setState({ students: data }, () => { console.log(this.state.students) });
                })
                .catch(e => console.log(e));
        }
    }

    courseName = (e) => {
        let cName = e.target.value;
        console.log(cName);
        this.setState({
            courseName: cName
        })
    }

    render() {

        const { isClickedNext } = this.state;

        return (
            <>
                {this.state.route ? <Redirect to={this.state.route} /> : null}
                <div className="p-5">
                    {isClickedNext === false ?
                        <>
                            <h1 className="main-title">Create new Class</h1>
                            <input type="text" placeholder="Class Name" onChange={this.courseName} className="new-input mt-3" required />
                            <br />
                            <Button onClick={this.next} id='submit' className="btn f3 grow btn-success next-btn mt-4">Next</Button>
                        </>
                        :
                        <AddStudents createNew={true} courseName={this.state.courseName} />
                    }
                </div>




                <div className="reg-content">
                    <div className="reg-container">


                        <ReactCardFlip className="flipCard" isFlipped={this.state.isFlipped} flipDirection="horizontal">
                            <>
                                <h1 className="main-title">Create new Class</h1>
                                <input type="text" placeholder="Class Name" onChange={this.courseName} className="new-input mt-3" required />
                                <br />
                                <Button onClick={this.next} id='submit' className="btn f3 grow btn-success next-btn mt-4">Next</Button>
                            </>



                            <div className="enregistrer active-section">
                                <div className="contact-form">
                                    <div className="reg-check reg-as">
                                        <label className="reg-as">
                                            <input id="checkAs" type="checkbox" className=" checkbox" onChange={this.asStdHandler} />
                                            <svg className='pointer' xmlns="http://www.w3.org/2000/svg" width="26px" height="23px">
                                                <path className="path-back" d="M1.5,6.021V2.451C1.5,2.009,1.646,1.5,2.3,1.5h18.4c0.442,0,0.8,0.358,0.8,0.801v18.398c0,0.442-0.357,0.801-0.8,0.801H2.3c-0.442,0-0.8-0.358-0.8-0.801V6" />
                                                <path className="path-moving" d="M24.192,3.813L11.818,16.188L1.5,6.021V2.451C1.5,2.009,1.646,1.5,2.3,1.5h18.4c0.442,0,0.8,0.358,0.8,0.801v18.398c0,0.442-0.357,0.801-0.8,0.801H2.3c-0.442,0-0.8-0.358-0.8-0.801V6" />
                                            </svg>
                                        </label>
                                        <label className="forAs pointer" htmlFor="checkAs" >Register As Student</label>
                                    </div>
                                    <div className="my-line"></div>

                                    {this.state.hideStdForm && <TeacherRegister hidden />}
                                    {!this.state.hideStdForm && <StudentRegister />}

                                </div>
                            </div>
                        </ReactCardFlip>

                    </div>

                </div>





            </>











        );
    }
}

export default CreateClass;
