import React, { Component } from 'react' ;
import img from '../img.jpeg';

class MyClasses extends Component{
    constructor(){
        super() ;
        this.state = {
            file: null
        }
    }
    checkAttendence = (e) => {
        console.log(URL.createObjectURL(e.target.files[0]));
        const img = e.target ;
        if(img){
            this.setState({
                file: URL.createObjectURL(e.target.files[0])
            })
            this.faceRecognition(img.files[0]) ;
        }
    }
    componentDidMount(){
        //left: 111, top: 11, right: 487, bottom: 387}
        let canvas = document.getElementById('canvas');
        let ctx = canvas.getContext('2d');

        ctx.strokeRect(111,11,376,376);


    }

    faceRecognition = (img) => {
        var myHeaders = new Headers();
        myHeaders.append("token", "0ed0d51e90cc4f3ab510a564cfb94b60");

        var formdata = new FormData();
        formdata.append("photo", img, "file");

        var requestOptions = {
        method: 'POST',
        headers: myHeaders,
        body: formdata,
        redirect: 'follow'
    };

        fetch("https://api.luxand.cloud/photo/search", requestOptions)
        .then(response => response.json())
        .then(result => {

            console.log(result) ;
        })
        .catch(error => console.log('error', error));
    };


    render() {

        return (
            <>
                <div className="mt-3 bg-black-10 shadow-5 p-5 ">
                    <h1 className="main-title">Upload an image to check attendence</h1>
                    <input onChange={this.checkAttendence} type="file" accept="image/*" id="file2" className="form-file mt-4" required />
                    <br />
                    <div className='draw-image'>
                    <img width="400" height="auto" className="mt-5 rounded" src={img||this.state.file || 'https://via.placeholder.com/400/'} alt='person'/><br/>
                    </div>
                    <canvas style ={{backgroundImage: `url(${img||this.state.file || 'https://via.placeholder.com/400/'})` }}id ='canvas' ></canvas>

                </div>
            </>);
    }
}
export default MyClasses ;
