import React from 'react';
import AddStudents from './AddStudents';

function EditClass({ classInfo }) {
    let { students } = classInfo;
    function deleteStd(id) {
        let conf = window.confirm("Are you sure you want to delete ..");
        if (conf) {
            students = students.filter(std => {
                return std.id !== id;
            })
            document.getElementById(`r${id}`).remove();
        }
    }

    let rows = classInfo.students.map((std) => {
        return (
            <tr id={`r${std.id}`} key={std.id}>
                <td>{std.id}</td>
                <td>{std.name}</td>
                <td><button onClick={() => deleteStd(std.id)}>X</button></td>
            </tr>
        )
    });
    function addStd(arr){
        // arr.reduce((acc,n)=>{
        //     if(acc.includes(n)){
        //     return acc;
        //     }
        //     return [].concat(acc,n);

        //     },[])
        // [{id:.. , name:...}]
        students.concat(arr);




    }

    return (
        <>
            <>
                <table className="table table-bordered" style={{ width: '100%' }}>
                    <thead className="text-dark" style={{ fontFamily: 'Lobster', letterSpacing: '2px' }}>
                        <tr>
                            <th>Student ID</th>
                            <th>Student Name</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody id='body' style={{ fontFamily: 'Acme', color: 'gray' }}>
                        {rows}
                    </tbody>
                </table>
            </>
            <AddStudents createNew={true} onAddStd={addStd} />
        </>
    )
}
export default EditClass;
