import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import {storage} from '../firebase/index';

class CreateClass extends Component {
    constructor() {
        super();
        this.state = {
            isClickedNext: false,
            image:null,
            url:''
        }
    }


    next = () => {
        this.setState({ isClickedNext: true });
    }

    listPerson=()=>{
        // const proxi = 'https://cors-anywhere.herokuapp.com/';
        const url = 'https://api.luxand.cloud/subject'
        // const fullUrl = proxi + url;
        fetch(url, {
            "async": true,
            "crossDomain": true,
            'method': 'GET',
            'headers': {
                "token": "0ed0d51e90cc4f3ab510a564cfb94b60"
            },
            "data": {}
        }).then(res => res.json())
            .then(console.log)
            .catch(err => console.log('error fetch'));
    }
    componentDidMount() {
        this.listPerson();

    }
    setImage=(e)=>{
        const im = e.target.files[0];
        if(im){
          this.setState({image:im});
          console.log(im);
        }
      }
      handleUpload=()=>{
        const {image} = this.state;
        if(image){

          const upload = storage.ref(`images/${image.name}`).put(image);
          upload.on('state_changed',
          (snapshot)=>{
              //loading bar
          },
          (err)=>{console.log(err);},
          ()=>{
            storage.ref('images').child(image.name).getDownloadURL().then(url=>{
              // let url = client.buildURL(urll, {faces:1, fit:'facearea',faceindex: 2,facepad: 1.2, q:75 });
              //let url = client.buildURL(urll, {facepad:'2',fit:'facearea',faceindex:12,w:'600',h:'600',q:'100'});
              // , faces:1 ,fm:'json'
              console.log(url);
            //   fetch(url).then(res=>res.json()).then(console.log).catch(console.log);
            //   this.setState({url})
            //   console.log(url);
            //   window.open(url,'_blank');
            })

          })
        }


      }

    render() {
        const { isClickedNext } = this.state;
        return (
            <>

                <div className="mt-3 bg-black-10 shadow-5 p-5 ">
                    {isClickedNext === false ? <>
                        <h1 className="main-title">Add Class</h1>
                        <input type="text" placeholder="Class Name" className="form-input mt-4" />
                        <br />
                        <Button onClick={this.next} id='submit' className="btn f3 grow btn-success btn-submit mt-4" >Next</Button>
                    </>
                        :
                        <>
                            <h1 className="main-title">Add Student</h1>
                            <input type="text" placeholder="Student Name" className="form-input mt-4" />
                            <br />
                            <input type="file" onChange={this.setImage} multiple accept="image/*" id="file2" placeholder="Student Major .." className="form-file mt-4" />
                            <br />
                            <Button id='submit' onClick={this.handleUpload} className="btn f3 grow btn-danger btn-submit mt-4" >Add Student</Button>
                            <Button id='submit' className="btn f3 grow btn-success btn-submit mt-4" >Done</Button><br />
                        </>}

                </div>
            </>);
    }
}

export default CreateClass;
